mapper.select:
SELECT 
FROM xxl_job_user AS t


		AND t.username like CONCAT(CONCAT('%', #{username}), '%')


		AND t.role = #{role}


ORDER BY username ASC
LIMIT #{offset}, #{pagesize}

SELECT *  from (
SELECT t1.*,rownum rn FROM (
SELECT 
FROM xxl_job_user t


		AND t.username like CONCAT(CONCAT('%', #{username}), '%')


		AND t.role = #{role}


ORDER BY username ASC
) t1
)
WHERE rn  >  ${offset} and rn  <=  ${offset}+#{pagesize}

SELECT *  from (
SELECT t1.*,ROW_NUMBER() OVER() rn FROM (
SELECT 
FROM xxl_job_user t


		AND t.username like CONCAT(CONCAT('%', #{username}), '%')


		AND t.role = #{role}


ORDER BY username ASC
) t1
) cc
WHERE cc.rn  >  ${offset} and cc.rn  <=  ${offset}+#{pagesize}

SELECT count(1) FROM xxl_job_user AS t
AND t.username like CONCAT(CONCAT('%', #{username}), '%')
AND t.role = #{role}

SELECT count(1) FROM xxl_job_user t
AND t.username like CONCAT(CONCAT('%', #{username}), '%')
AND t.role = #{role}

SELECT count(1) FROM xxl_job_user t
AND t.username like CONCAT(CONCAT('%', #{username}), '%')
AND t.role = #{role}

SELECT 
FROM xxl_job_user AS t
WHERE t.username = #{username}

SELECT 
FROM xxl_job_user t
WHERE t.username = #{username}

SELECT 
FROM xxl_job_user t
WHERE t.username = #{username}

mapper.insert:
INSERT INTO xxl_job_user (
	username,
	password,
	role,
	permission
) VALUES (
	#{username},
	#{password},
	#{role},
	#{permission}
);

mapper.update:
UPDATE xxl_job_user
SET

		password = #{password},

	role = #{role},
	permission = #{permission}
WHERE id = #{id}

UPDATE xxl_job_user
SET

	password = #{password, jdbcType=VARCHAR},

role = #{role, jdbcType=NUMERIC},
permission = #{permission, jdbcType=VARCHAR}
WHERE id = #{id}

UPDATE xxl_job_user
SET

	password = #{password, jdbcType=VARCHAR},

role = #{role, jdbcType=NUMERIC},
permission = #{permission, jdbcType=VARCHAR}
WHERE id = #{id}
